vwap.script:
var src = ($price.high + $price.low + $price.close)/3
var vol=vbuy+vsell
var sumVol = cum(vol)
var sumSrcVol = cum(src * vol)
var sumSrcSrcVol = cum(vol*Math.pow(src, 2))
var VWAP = sumSrcVol / sumVol 
var variance = sumSrcSrcVol / sumVol - Math.pow(VWAP, 2)
var stDev = Math.sqrt(variance < 0 ? 0 : variance)

line(VWAP)
cloudarea(VWAP - stDev * options.stdevMultiplier, VWAP + stDev * options.stdevMultiplier)

var unixDays = time / 60 / 60 / 24 + 3
if (unixDays % (options.sessionLengthInDays) === 0) {
  fns[0].state = {}
  fns[1].state = {}
  fns[2].state = {}
}

rsi.script:
// rsi
src(50) = $price.close
var change = src - src[1]
var up = ema(Math.max(change, 0), options.length)
var down = ema(-Math.min(change, 0), options.length)
rsi = down == 0 ? 100 : up == 0 ? 0 : 100 - (100 / (1 + up / down))
line(rsi)
brokenarea(options.thresholdUp, options.thresholdLow, color=options.thresholdColor)

if (options.showDivs) {
  // <markers utils>
  if (markers === 0) {
    markers = []
  }

  if (pendingMarker && pendingMarker.time < time) {
    markers.push(pendingMarker)
    pendingMarker = null
  }

  var newMarker = null
  // </markers utils>

  // find long signals
  pl = pivot_low(rsi, options.pivotLeftLength, options.pivotRightLength)
  if (pl && pl !== _pl) {
    var plsrc = src[options.pivotRightLength]
    if (pl > _pl && plsrc < _plsrc && (pl < options.thresholdLow || _pl < options.thresholdLow)) {
      newMarker = {
        time: time - options.pivotRightLength * bar.timeframe,
        position: 'belowBar',
        text: 'ðŸ”º',
        size: 1
      }
    }

    _plsrc = plsrc
    _pl = pl
  }

  // find sell signals
  ph = pivot_high(rsi, options.pivotLeftLength, options.pivotRightLength)

  if (ph && ph !== _ph) {
    var phsrc = src[options.pivotRightLength]
    if (ph < _ph && phsrc > _phsrc && (ph > options.thresholdUp || _ph > options.thresholdUp)) {
      newMarker = {
        time: time - options.pivotRightLength * bar.timeframe,
        position: 'aboveBar',
        text: 'ðŸ”»',
        size: 1
      }
    }

    _phsrc = phsrc
    _ph = ph
  }

  // <markers utils>
  if (newMarker || (pendingMarker && !newMarker)) {
    pendingMarker = newMarker

    if (markers.length > 200) {
      markers.splice(0, 1)
    }

    if (series[0].setMarkers) {
      series[0].setMarkers(markers.concat(pendingMarker))
    }
  }
  // </markers utils>
}

pivot-vwap.script:
ph = pivot_high($price.close, options.pivotLeftLength, options.pivotRightLength)
pl = pivot_low($price.close, options.pivotLeftLength, options.pivotRightLength)


srcHigh(50) = $price.close
vHigh(50) = vsell + vbuy

if (ph && _ph !== ph) {
  if (ph > _ph) {
    var lookbackLength = (options.pivotRightLength+options.pivotRightLength)/2

    var _volHigh = 0
    var _srcVolHigh  = 0
    var _srcSrcVolHigh  = 0

    for (var i = lookbackLength - 1; i > 0; i--) {
      var src = srcHigh[i]
      var vol = vHigh[i]
      _volHigh = _volHigh + vol
      _srcVolHigh = _srcVolHigh + src * vol
      _srcSrcVolHigh = _srcSrcVolHigh + vol * Math.pow(src, 2)
    }

    fns[2].state.open = _volHigh
    fns[3].state.open = _srcVolHigh
    fns[4].state.open = _srcSrcVolHigh
  }

  _ph = ph
}

var volHigh = cum(vHigh)
var srcVolHigh = cum(srcHigh * vHigh)
var srcSrcVolHigh = cum(vHigh * Math.pow(srcHigh, 2))
var vwapHigh = srcVolHigh / volHigh 
var varianceHigh = srcSrcVolHigh  / volHigh - Math.pow(vwapHigh, 2)
var stDevHigh = Math.sqrt(varianceHigh )

srcLow(50) = $price.close
vLow(50) = vbuy + vsell

if (pl && _pl !== pl) {
  if (pl < _pl) {
    var lookbackLength = (options.pivotRightLength+options.pivotRightLength)/2

    var _volLow = 0
    var _srcVolLow  = 0
    var _srcSrcVolLow  = 0

    for (var i = lookbackLength - 1; i > 0; i--) {
      var src = srcLow[i]
      var vol = vLow[i]
      _volLow = _volLow + vol
      _srcVolLow = _srcVolLow + src * vol
      _srcSrcVolLow = _srcSrcVolLow + vol * Math.pow(src, 2)
    }

    fns[5].state.open = _volLow
    fns[6].state.open = _srcVolLow
    fns[7].state.open = _srcSrcVolLow
  }

  _pl = pl
}

var volLow = cum(vLow)
var srcVolLow = cum(srcLow * vLow)
var srcSrcVolLow = cum(vLow * Math.pow(srcLow, 2))
var vwapLow = srcVolLow / volLow
var varianceLow = srcSrcVolLow / volLow- Math.pow(vwapLow, 2)
var stDevLow = Math.sqrt(varianceLow)

if (options.showStdBands) {
  cloudarea({
    time: time,
    higherValue: vwapHigh + stDevHigh * options.devMultA1,
    lowerValue: vwapHigh + stDevHigh * options.devMultA2
  },
    positiveColor= options.negativeColor,
    negativeColor= options.negativeColor,
    higherLineColor= options.higherLineColor,
    lowerLineColor= options.higherLineColor
  )

  cloudarea({
    time: time,
    higherValue: vwapHigh + stDevHigh * options.devMultB1,
    lowerValue: vwapHigh + stDevHigh * options.devMultB2
  },
    positiveColor= options.negativeColor,
    negativeColor= options.negativeColor,
    higherLineColor= options.higherLineColor,
    lowerLineColor= options.higherLineColor
  )

  cloudarea({
    time: time,
    higherValue: vwapLow - stDevLow * options.devMultA1,
    lowerValue: vwapLow - stDevLow * options.devMultA2
  },
    positiveColor= options.positiveColor,
    negativeColor= options.positiveColor,
    higherLineColor= options.lowerLineColor,
    lowerLineColor= options.lowerLineColor
  )

  cloudarea({
    time: time,
    higherValue: vwapLow - stDevLow * options.devMultB1,
    lowerValue: vwapLow - stDevLow * options.devMultB2
  },
    positiveColor = options.positiveColor,
    negativeColor = options.positiveColor,
    higherLineColor = options.lowerLineColor,
    lowerLineColor = options.lowerLineColor
  )
}

if (options.showVwap) {
  cloudarea(vwapLow, vwapHigh)
}

if (options.showPivot) {
  line(_ph, offset=(-options.pivotRightLength-options.pivotRightLength)/2)
  line(_pl, offset=(-options.pivotRightLength-options.pivotRightLength)/2)
}
