volume.script:
if (upColor === 0) {
  if (options.showDelta) {
    upColor = options.upBgColor
    downColor = options.downBgColor
  } else {
    upColor = options.upColor
    downColor = options.downColor
  }
}

if (options.showDelta) {
  plothistogram({ time: time, value: Math.abs(vbuy-vsell), color: vbuy - vsell > 0 ? options.upColor : options.downColor})
}

plothistogram({ time: time, value: vbuy + vsell, color: vbuy > vsell ? upColor : downColor })

vwap.script:
var src = ($price.high + $price.low + $price.close)/3
var vol=vbuy+vsell
var sumVol = cum(vol)
var sumSrcVol = cum(src * vol)
var sumSrcSrcVol = cum(vol*Math.pow(src, 2))
var VWAP = sumSrcVol / sumVol 
var variance = sumSrcSrcVol / sumVol - Math.pow(VWAP, 2)
var stDev = Math.sqrt(variance < 0 ? 0 : variance)

line(VWAP)
cloudarea(VWAP - stDev * options.stdevMultiplier, VWAP + stDev * options.stdevMultiplier)

var unixDays = time / 60 / 60 / 24 + 3
if (unixDays % (options.sessionLengthInDays) === 0) {
  fns[0].state = {}
  fns[1].state = {}
  fns[2].state = {}
}

rsi.script:
// rsi
src(50) = $price.close
var change = src - src[1]
var up = ema(Math.max(change, 0), options.length)
var down = ema(-Math.min(change, 0), options.length)
rsi = down == 0 ? 100 : up == 0 ? 0 : 100 - (100 / (1 + up / down))
line(rsi)
brokenarea(options.thresholdUp, options.thresholdLow, color=options.thresholdColor)

if (options.showDivs) {
  // <markers utils>
  if (markers === 0) {
    markers = []
  }

  if (pendingMarker && pendingMarker.time < time) {
    markers.push(pendingMarker)
    pendingMarker = null
  }

  var newMarker = null
  // </markers utils>

  // find long signals
  pl = pivot_low(rsi, options.pivotLeftLength, options.pivotRightLength)
  if (pl && pl !== _pl) {
    var plsrc = src[options.pivotRightLength]
    if (pl > _pl && plsrc < _plsrc && (pl < options.thresholdLow || _pl < options.thresholdLow)) {
      newMarker = {
        time: time - options.pivotRightLength * bar.timeframe,
        position: 'belowBar',
        text: 'ðŸ”º',
        size: 1
      }
    }

    _plsrc = plsrc
    _pl = pl
  }

  // find sell signals
  ph = pivot_high(rsi, options.pivotLeftLength, options.pivotRightLength)

  if (ph && ph !== _ph) {
    var phsrc = src[options.pivotRightLength]
    if (ph < _ph && phsrc > _phsrc && (ph > options.thresholdUp || _ph > options.thresholdUp)) {
      newMarker = {
        time: time - options.pivotRightLength * bar.timeframe,
        position: 'aboveBar',
        text: 'ðŸ”»',
        size: 1
      }
    }

    _phsrc = phsrc
    _ph = ph
  }

  // <markers utils>
  if (newMarker || (pendingMarker && !newMarker)) {
    pendingMarker = newMarker

    if (markers.length > 200) {
      markers.splice(0, 1)
    }

    if (series[0].setMarkers) {
      series[0].setMarkers(markers.concat(pendingMarker))
    }
  }
  // </markers utils>
}

pivot-vwap.script:
ph = pivot_high($price.high, options.pivotLeftLength, options.pivotRightLength)
pl = pivot_low($price.low, options.pivotLeftLength, options.pivotRightLength)

srcClose(100) = $price.close
srcLow(100) = $price.low
srcHigh(100) = $price.high
vol(100) = vbuy + vsell

if (ph && _ph !== ph) {  
  if (ph > _ph && ((!options.showBands && ph > vwapHighHigh) || (options.showBands && ph > highestBand))) {
    var _vol = 0
    var _srcVolClose  = 0
    var _srcSrcVolClose  = 0
    var _srcVolHigh  = 0
    var _srcSrcVolHigh  = 0
    var _srcVolLow  = 0
    var _srcSrcVolLow  = 0

    for (var i = options.pivotRightLength; i > 0; i--) {
      var srcAtIndex = srcClose[i]
      var volAtIndex = vol[i]
      _vol = _vol + volAtIndex 
      _srcVolClose = _srcVolClose + srcAtIndex * volAtIndex
      _srcSrcVolClose = _srcSrcVolClose + volAtIndex * Math.pow(srcAtIndex, 2)

      srcAtIndex = srcHigh[i]
      _srcVolHigh = _srcVolHigh + srcAtIndex * volAtIndex
      _srcSrcVolHigh = _srcSrcVolHigh + volAtIndex * Math.pow(srcAtIndex, 2)

      srcAtIndex = srcLow[i]
      _srcVolLow = _srcVolLow + srcAtIndex * volAtIndex
      _srcSrcVolLow = _srcSrcVolLow + volAtIndex * Math.pow(srcAtIndex, 2)
    }

    fns[2].state.open = _vol 
    fns[3].state.open = _srcVolClose
    fns[4].state.open = _srcVolHigh
    fns[5].state.open = _srcVolLow
    fns[6].state.open = _srcSrcVolClose
    fns[7].state.open = _srcSrcVolHigh
    fns[8].state.open = _srcSrcVolLow
  }

  _ph = ph
}

var volHigh = cum(vol)
var srcVolHighClose = cum(srcClose * vol)
var srcVolHighHigh = cum(srcHigh * vol)
var srcVolHighLow = cum(srcLow * vol)
vwapHighClose = srcVolHighClose / volHigh 
vwapHighHigh = srcVolHighHigh / volHigh 
vwapHighLow = srcVolHighLow / volHigh 

if (options.showBands) {
  var srcSrcVolHighClose = cum(vol * Math.pow(srcClose, 2))
  var srcSrcVolHighHigh = cum(vol * Math.pow(srcHigh, 2))
  var srcSrcVolHighLow = cum(vol * Math.pow(srcLow, 2))
  var stDevHighClose = Math.sqrt(srcSrcVolHighClose / volHigh - Math.pow(vwapHighClose, 2))
  var stDevHighHigh = Math.sqrt(srcSrcVolHighHigh / volHigh - Math.pow(vwapHighHigh, 2))
  var stDevHighLow = Math.sqrt(srcSrcVolHighLow / volHigh - Math.pow(vwapHighLow, 2))
  highestBand = vwapHighClose + stDevHighClose * options.bandMultiplier

  cloudarea(vwapHighLow + stDevHighLow * options.bandMultiplier, vwapHighHigh + stDevHighHigh * options.bandMultiplier)
}

if (pl && _pl !== pl) {
  if (pl < _pl && ((!options.showBands && pl < vwapLowLow) || (options.showBands && pl < lowestBand))) {
    var _vol = 0
    var _srcVolClose  = 0
    var _srcSrcVolClose  = 0
    var _srcVolHigh  = 0
    var _srcSrcVolHigh  = 0
    var _srcVolLow  = 0
    var _srcSrcVolLow  = 0

    for (var i = options.pivotRightLength; i > 0; i--) {
      var srcAtIndex = srcClose[i]
      var volAtIndex = vol[i]
      _vol = _vol + volAtIndex 
      _srcVolClose = _srcVolClose + srcAtIndex * volAtIndex
      _srcSrcVolClose = _srcSrcVolClose + volAtIndex * Math.pow(srcAtIndex, 2)

      srcAtIndex = srcHigh[i]
      _srcVolHigh = _srcVolHigh + srcAtIndex * volAtIndex
      _srcSrcVolHigh = _srcSrcVolHigh + volAtIndex * Math.pow(srcAtIndex, 2)

      srcAtIndex = srcLow[i]
      _srcVolLow = _srcVolLow + srcAtIndex * volAtIndex
      _srcSrcVolLow = _srcSrcVolLow + volAtIndex * Math.pow(srcAtIndex, 2)
    }

    fns[9].state.open = _vol 
    fns[10].state.open = _srcVolClose
    fns[11].state.open = _srcVolHigh
    fns[12].state.open = _srcVolLow
    fns[13].state.open = _srcSrcVolClose
    fns[14].state.open = _srcSrcVolHigh
    fns[15].state.open = _srcSrcVolLow
  }

  _pl = pl
}

var volLow = cum(vol)
var srcVolLowClose = cum(srcClose * vol)
var srcVolLowHigh = cum(srcHigh * vol)
var srcVolLowLow = cum(srcLow * vol)
vwapLowClose = srcVolLowClose / volLow 
vwapLowHigh = srcVolLowHigh / volLow 
vwapLowLow = srcVolLowLow / volLow 

if (options.showBands) {
  var srcSrcVolLowClose = cum(vol * Math.pow(srcClose, 2))
  var srcSrcVolLowHigh = cum(vol * Math.pow(srcHigh, 2))
  var srcSrcVolLowLow = cum(vol * Math.pow(srcLow, 2))
  var stDevLowClose = Math.sqrt(srcSrcVolLowClose / volLow - Math.pow(vwapLowClose, 2))
  var stDevLowHigh = Math.sqrt(srcSrcVolLowHigh / volLow - Math.pow(vwapLowHigh, 2))
  var stDevLowLow = Math.sqrt(srcSrcVolLowLow / volLow - Math.pow(vwapLowLow, 2))
  lowestBand = vwapLowClose - stDevLowClose * options.bandMultiplier

  cloudarea(vwapLowLow - stDevLowLow * options.bandMultiplier, vwapLowHigh - stDevLowHigh * options.bandMultiplier)
}

if (options.showMedianVwap) {
  line((vwapLowClose + vwapLowHigh + vwapLowLow + vwapHighClose + vwapHighHigh + vwapHighLow) / 6)
}

if (options.showPivot) {
  if (_ph) line(_ph, offset=(-options.pivotRightLength-options.pivotRightLength)/2)
  if (_pl) line(_pl, offset=(-options.pivotRightLength-options.pivotRightLength)/2)
}

sma-bands.script:
var vol = vbuy + vsell
var length = options.length || 200

var volSma = sma(vol, options.length)
var srcVolSmaClose = sma($price.close * vol, options.length)
var srcVolSmaHigh = sma($price.high * vol, options.length)
var srcVolSmaLow = sma($price.low * vol, options.length)
vwapHighClose = srcVolSmaClose / volSma 
vwapHighHigh = srcVolSmaHigh / volSma 
vwapHighLow = srcVolSmaLow / volSma 

if (options.showHLArea) {
  cloudarea(vwapHighHigh, vwapHighLow)
}

if (options.showClose) {
  line(vwapHighClose)
}

if (options.showBands) {
  var srcSrcVolSmaClose = sma(vol * Math.pow($price.close, 2), options.length)
  var srcSrcVolSmaHigh = sma(vol * Math.pow($price.high, 2), options.length)
  var srcSrcVolSmaLow = sma(vol * Math.pow($price.low, 2), options.length)
  var stDevHighClose = Math.sqrt(srcSrcVolSmaClose / volSma - Math.pow(vwapHighClose, 2))
  var stDevHighHigh = Math.sqrt(srcSrcVolSmaHigh / volSma - Math.pow(vwapHighHigh, 2))
  var stDevHighLow = Math.sqrt(srcSrcVolSmaLow / volSma - Math.pow(vwapHighLow, 2))

  if (options.showHLArea) {
    cloudarea(vwapHighLow - stDevHighLow * options.bandMultiplier, vwapHighHigh - stDevHighHigh * options.bandMultiplier)
    cloudarea(vwapHighLow + stDevHighLow * options.bandMultiplier, vwapHighHigh + stDevHighHigh * options.bandMultiplier)
  }

  if (options.showClose) {
    line(vwapHighClose - stDevHighClose * options.bandMultiplier)
    line(vwapHighClose + stDevHighClose * options.bandMultiplier)
  }
}

vwap-bands.script:
var price = $price
var src = (price.high + price.low + price.close)/3

var sumSell = sum(vsell, options.length)
var sumSrcSell = sum(src * vsell, options.length)
var sumSrcSrcSell = sum(vsell*Math.pow(src, 2), options.length)
var VWAPSell = sumSrcSell / sumSell 
var varianceSell = sumSrcSrcSell / sumSell - Math.pow(VWAPSell, 2)
var stDevSell = Math.sqrt(varianceSell < 0 ? 0 : varianceSell)
var sumBuy = sum(vbuy, options.length)
var sumSrcBuy = sum(src * vbuy, options.length)
var sumSrcSrcBuy = sum(vbuy*Math.pow(src, 2), options.length)
var VWAPBuy = sumSrcBuy / sumBuy 
var varianceBuy = sumSrcSrcBuy / sumBuy - Math.pow(VWAPBuy, 2)
var stDevBuy = Math.sqrt(varianceBuy < 0 ? 0 : varianceBuy)

var thresholdDown = VWAPBuy - stDevBuy * options.stdevMultiplier
var thresholdUp = VWAPSell + stDevSell * options.stdevMultiplier

cloudarea(
  VWAPSell - stDevSell * options.stdevMultiplier,
  thresholdDown,
  positiveColor=options.buyBgColor,
  negativeColor=options.buyBgColor,
  higherLineColor=options.buyColor,
  lowerLineColor=options.buyColor)

cloudarea(thresholdUp,
  VWAPBuy + stDevBuy * options.stdevMultiplier,
  positiveColor=options.sellBgColor,
  negativeColor=options.sellBgColor,
  higherLineColor=options.sellColor,
  lowerLineColor=options.sellColor)

var na = { time: time }

if (options.showDouble) {
  cloudarea(
    VWAPSell - stDevSell * options.stdevSecondaryMultiplier,
    thresholdDown,
    positiveColor=options.dblBuyBgColor,
    negativeColor=options.dblBuyBgColor,
    higherLineColor=options.dblBuyColor,
    lowerLineColor=options.dblBuyColor
  )

  cloudarea(
   thresholdUp,
   VWAPBuy + stDevBuy * options.stdevSecondaryMultiplier,
    positiveColor=options.dblSellBgColor,
    negativeColor=options.dblSellBgColor,
    higherLineColor=options.dblSellColor,
    lowerLineColor=options.dblSellColor
  )
}
